/* Paiat : parallax almost in a tweet by ruffiem */$(document).ready(function(){$(window).on("scroll load",function(){$(".paiat").each(function(){var o=$(this),t=o.parent("div").offset(),n=o.parent("div").height(),a=o.data("speed"),e=t.top-$(window).height(),d=t.top+n,i=$(document).scrollTop();e>i||i>d||o.css({top:(i-e)*a*-1})})})});